import React from 'react';
import { Container, Row, Col, Card, Badge } from 'react-bootstrap'; /* trovati online */
import jsonData from '../data/d2-json/fantasy.json';

const LatestReleasePage = () => {
    return (
        <div>
            <h2 className="text-center mb-5">Ultimi Arrivi</h2>
            <Container fluid>
                <Row xs={1} sm={2} md={3} lg={4}> {/* numero colonne per ogni formato schermo */}
                    {jsonData.map((book) => (
                        <Col key={book.id}>
                            <Card>
                                <Card.Img src={book.img} alt={book.title} />
                                <Card.Body>
                                    <Card.Title className="text-truncate">{book.title}</Card.Title>
                                    <Card.Subtitle className="mb-2">{book.author}</Card.Subtitle>
                                    <Card.Text className="mb-1">{book.category}</Card.Text>
                                    <Badge bg="primary" className="mb-2">â‚¬{book.price}</Badge>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}

                </Row>
            </Container>
        </div>
    );
};

export default LatestReleasePage;
