import { Container, Col, Row, Card, Badge } from 'react-bootstrap'; /* trovati online */
import jsonData from '../data/d2-json/fantasy.json';
import "../Layout/BookCard.css"
import MyBadge from './MyBadge';
import React,{useState} from 'react';

const LatestReleasePage = () => {

    const [selected, setSelected] = useState(false)
    console.log(selected)
    const toggleSelected = () => {
        setSelected(!selected)//voglio che sia sempre il contrario di quello che è attualmente
    }

    return (
        <div>
            <h4 className="text-center mb-5">Ultimi Arrivi</h4>
           <>
           <MyBadge
                str="Ciao"
                color="primary"
           />
                <Container fluid>
                    <Row xs={1} sm={2} md={3} lg={4} className="g-3" onClick={toggleSelected}>
                        {jsonData.map((book) => (
                                <Card className={`${selected ? 'border border-danger shadow' : null}`}> {/* non riesco a modificare altezza, la voglio fissa */}
                                    <Card.Img className="object-fit-cover w-100 book-card" src={book.img} alt={book.title} />
                                    <Card.Body key={book.asin}>    
                                        <Card.Title className="text-truncate">{book.title}</Card.Title>
                                        <Card.Subtitle className="mb-2">{book.author}</Card.Subtitle>
                                        <Card.Text className="mb-1">{book.category.charAt(0).toUpperCase() + book.category.slice(1)}</Card.Text>
                                        <Badge bg="primary" className="mb-2">€{book.price}</Badge>
                                    </Card.Body>
                                </Card>
                        ))}
                    </Row>
                </Container>
            </>
        </div>
    );
};

export default LatestReleasePage;
