import React from 'react';
import { Container, Row, Col, Card, Badge } from 'react-bootstrap'; /* trovati online */
import jsonData from '../data/LatestRelease-json/fantasy.json';

const LatestReleasePage = () => {
  return (
    <div>
      <h2 className="text-center mb-5">Ultimi Arrivi</h2>
      <Container fluid>
        <Row xs={1} sm={2} md={3} lg={4} className="g-4">
          {jsonData.map((book) => (
            <Col key={book.id}>
              <Card className="h-100">
                <Card.Img variant="top" src={book.img} className="cover" alt={book.title} />
                <Card.Body>
                  <Card.Title className="text-truncate">{book.title}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">{book.author}</Card.Subtitle>
                  <Card.Text className="mb-1">{book.category}</Card.Text>
                  <Badge bg="primary" className="mb-2">â‚¬{book.price}</Badge>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default LatestReleasePage;
