import React from 'react';
import jsonData from '../data/LatestRelease-json/fantasy.json';

const LatestReleasePage = () => {
  return (
    <div>
      <h2 className="text-center mb-5">Ultimi Arrivi</h2>
      <div className="container">
        <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 row-cols-lg-4">
          {jsonData.map((book) => (
            <div className="col mb-4" key={book.id}>
              <div className="card h-100">
                  <img src={book.img} className="card-img-top cover" alt={book.title} />
                  <div className="card-body">
                    <h5 className="card-title text-truncate">{book.title}</h5>
                    <p className="card-text">{book.author}</p>
                    <p className="card-text">{book.category}</p>
                  </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LatestReleasePage;
